        public string CreatePdf(PdfData pdfData)
        {
            if (GlobalFontSettings.FontResolver == null)
            {
                GlobalFontSettings.FontResolver = new FontResolver(_resourcesPath);
            }

            LoadImageSignature(pdfData.Barcode);
            //LoadImageQr("R0lGODlhAQABAIAAAAAAAAAAACH5BAAAAAAALAAAAAABAAEAAAICTAEAOw==");

            var document = new PdfDocument();
            var page = document.AddPage();
            page.Orientation = PdfSharp.PageOrientation.Landscape;
            var gfx = XGraphics.FromPdfPage(page);

            //AddTitleLogo(gfx, page, $"{_imagesPath}\\logo.jpg", 1, 1);
            AddBackground(gfx, page, $"{_imagesPath}\\bg.jpg", 0, 0);
            AddTitleAndFooter(page, gfx, "NO: " + pdfData.DocCode, document, pdfData);
            AddDescription(gfx, pdfData);

            AddTrainee(gfx, pdfData);
            AddTraineeJobTitle(gfx, pdfData);

            AddCompany(gfx, pdfData);

            AddTrainer(gfx, pdfData);
            AddTrainingDate(gfx, pdfData);

            // Get an XGraphics object for drawing



            DrawImage(gfx, $"{_imagesPath}\\qrr.jpg", 50, 480, 80, 80);
            DrawImage(gfx, $"{_imagesPath}\\signature.jpg", 320, 450, 150, 70);
            //string filePath = "R0lGODlhAQABAIAAAAAAAAAAACH5BAAAAAAALAAAAAABAAEAAAICTAEAOw";
            //File.WriteAllBytes(filePath, Convert.FromBase64String("R0lGODlhAQABAIAAAAAAAAAAACH5BAAAAAAALAAAAAABAAEAAAICTAEAOw"));
            //DrawImage(gfx, Im, 345, 450, 150, 70);
            //ImgQR("");


            //string docName = $"{_createdDocsPath}/{pdfData.DocumentTitle}-{DateTime.UtcNow.ToOADate()}.pdf";
            string docName = $"{_createdDocsPath}/{pdfData.DocumentTitle}.pdf";
            //try
            //{
            //    document.Save(docName);
            //}
            //catch {
            //}



            page = document.AddPage();
            page.Orientation = PdfSharp.PageOrientation.Landscape;
            gfx = XGraphics.FromPdfPage(page);

            //AddTitleLogo(gfx, page, $"{_imagesPath}\\logo.jpg", 1, 1);
            AddBackground(gfx, page, $"{_imagesPath}\\bg-back.jpg", 0, 0);
            AddDescription2(gfx, pdfData);

            for (int i = 0; i < pdfData.Topics.Count; i++)
            {
                AddCurriculum(gfx, pdfData.Topics[i], i * 20);
            }

            string docName2 = $"{_createdDocsPath}/{pdfData.DocumentTitle}.pdf";
            try
            {
                document.Save(docName);
            }
            catch
            {
            }



            return docName;
        }





        void DrawImage(XGraphics gfx, string jpegSamplePath, int x, int y, int width, int height)
        {
            XImage image = XImage.FromFile(jpegSamplePath);

            gfx.DrawImage(image, x, y, width, height);
        }

        void AddTitleLogo(XGraphics gfx, PdfPage page, string imagePath, int xPosition, int yPosition)
        {
            if (!File.Exists(imagePath))
            {
                throw new FileNotFoundException(String.Format("Could not find image {0}.", imagePath));
            }

            XImage xImage = XImage.FromFile(imagePath);
            gfx.DrawImage(xImage, xPosition, yPosition, xImage.PixelWidth / 8, xImage.PixelHeight / 8);
        }

        void AddBackground(XGraphics gfx, PdfPage page, string imagePath, int xPosition, int yPosition)
        {
            if (!File.Exists(imagePath))
            {
                throw new FileNotFoundException(String.Format("Could not find image {0}.", imagePath));
            }

            XImage xImage = XImage.FromFile(imagePath);
            gfx.DrawImage(xImage, xPosition, yPosition, page.Width, page.Height);
        }

        void AddTitleAndFooter(PdfPage page, XGraphics gfx, string title, PdfDocument document, PdfData pdfData)
        {
            XRect rect = new XRect(new XPoint(), gfx.PageSize);
            rect.Inflate(-10, -15);
            rect.Offset(-30, 5);
            XFont font = new XFont("OpenSans", 13, XFontStyle.Regular);
            gfx.DrawString(title, font, XBrushes.White, rect, XStringFormats.TopRight);

            XRect rect2 = new XRect(new XPoint(), gfx.PageSize);
            rect2.Inflate(-10, -15);
            rect2.Offset(0, -10);
            font = new XFont("OpenSans", 16, XFontStyle.Bold);
            XStringFormat format = new XStringFormat();
            format.Alignment = XStringAlignment.Center;
            format.LineAlignment = XLineAlignment.Far;
            gfx.DrawString(pdfData.TrainerJobTitle, font, XBrushes.DarkBlue, rect2, format);

            //font = new XFont("OpenSans", 8);
            //format.Alignment = XStringAlignment.Center;
            //gfx.DrawString(document.PageCount.ToString(), font, XBrushes.DarkOrchid, rect, format);

            document.Outlines.Add(title, page, true);
        }

